+package com.sequenceiq.periscope.monitor;
+
+import java.util.List;
+
+import org.springframework.boot.autoconfigure.condition.ConditionalOnProperty;
+import org.springframework.stereotype.Component;
+
+import com.sequenceiq.periscope.api.model.ClusterState;
+import com.sequenceiq.periscope.domain.Cluster;
+import com.sequenceiq.periscope.monitor.evaluator.ClusterStateEvaluator;
+
+@Component("RemovableClusterMonitor")
+@ConditionalOnProperty(prefix = "periscope.enabledAutoscaleMonitors.removable-cluster-monitor", name = "enabled", havingValue = "true")
+public class RemovableClusterMonitor extends ClusterMonitor {
+
+    @Override
+    public String getIdentifier() {
+        return "removable-cluster-monitor";
+    }
+
+    @Override
+    public String getTriggerExpression() {
+        return MonitorUpdateRate.EVERY_MIN_RATE_CRON;
+    }
+
+    @Override
+    public Class<?> getEvaluatorType(Cluster cluster) {
+        return ClusterStateEvaluator.class;
+    }
+
+    @Override
+    protected List<Cluster> getMonitored() {
+        //To monitor Suspended & Clusters with AutoScaling disabled and purge them in periscope when they are deleted in CB.
+        List removableClustersCheck = getClusterService()
+                .findAllByStateAndNode(ClusterState.SUSPENDED, getPeriscopeNodeConfig().getId());
+        removableClustersCheck.addAll(getClusterService()
+                        .findAllByStateAndNode(ClusterState.PENDING, getPeriscopeNodeConfig().getId()));
+        removableClustersCheck.addAll(getClusterService()
+                .findAllForNode(ClusterState.RUNNING, false, getPeriscopeNodeConfig().getId()));
+        return removableClustersCheck;
+    }
