refactor suggestion: break up into smaller methods
    public void provide(RestCallDetails restCallDetails, CDPOperationDetails operationDetails, Map<String, String> restParams,
            String nameField, String crnField) {
        try {
            Optional<NameValuePair> environmentValue = extractEnvironmentValue(restCallDetails);
            environmentValue.ifPresent(nameValuePair -> updateRestParams(restParams, nameField, crnField, nameValuePair));
        } catch (URISyntaxException e) {
            LOGGER.warn("Cannot provide name and crn because the uri is invalid or any error occurred: {}", e.getMessage(), e);
        }
    }

    private void updateRestParams(Map<String, String> restParams, String nameField, String crnField, NameValuePair nameValuePair) {
        String accountId = ThreadBasedUserCrnProvider.getAccountId();
        Stack stack = stackService.getByEnvironmentCrnAndAccountIdEvenIfTerminated(nameValuePair.getValue(), accountId);
        restParams.put(nameField, stack.getName());
        restParams.put(crnField, stack.getResourceCrn());
        restParams.put(RESOURCE_ID, stack.getId().toString());
    }

    private Optional<NameValuePair> extractEnvironmentValue(RestCallDetails restCallDetails) throws URISyntaxException {
        String requestUri = restCallDetails.getRestRequest().getRequestUri();
        URI uri = new URI(requestUri);
        List<NameValuePair> queryParams = URLEncodedUtils.parse(uri, StandardCharsets.UTF_8);
        Optional<NameValuePair> environmentValue = queryParams.stream().filter(p -> "environment".equals(p.getName())).findFirst();
        return environmentValue;
    }

