 public Code(final EVM evm, final int contractAccountVersion, final Bytes bytes) {
    this.bytes = bytes;

    validJumpDestinations = Suppliers.memoize(()-> {
      final BitSet bitSet = new BitSet(getSize());
      evm.forEachOperation(
              this,
              contractAccountVersion,
              (final Operation op, final Integer offset) -> {
                if (op.getOpcode() == JumpDestOperation.OPCODE) {
                  bitSet.set(offset);
                }
              });
      return bitSet;
    });

    validJumpSubDestinations = Suppliers.memoize(()-> {
      final BitSet bitSet = new BitSet(getSize());
      evm.forEachOperation(
              this,
              contractAccountVersion,
              (final Operation op, final Integer offset) -> {
                if (op.getOpcode() == BeginSubOperation.OPCODE) {
                  bitSet.set(offset);
                }
              });
      return bitSet;
    });
  }

