+    }
+
+
+    private void assertPluginEnabled(String name) {
+        PluginWrapper plugin = j.getPluginManager().getPlugin(name);
+        assertThat(plugin, is(notNullValue()));
+        assertTrue(plugin.isEnabled());
+    }
+
+    // End of DisablePluginCommandTest
