+/* pane */
+
+.pane-frame {
+  border: var(--pane-border-width) solid var(--pane-border-color);
+  border-radius: 4px;
+}
+
+.pane-header, .pane-footer {
+  padding: 8px 0px;
+  color: var(--pane-header-color);
+  background: var(--pane-header-bg);
+  // TODO: see how this affects the side panel widgets
+  border: var(--pane-border-width) solid var(--pane-header-border-color);
+  border-left: none;
+  border-right: none;
+  color: var(--pane-text-color);
+}
+
+.pane-header {
+  border-top: none;
+  border-top-left-radius: 4px;
+  border-top-right-radius: 4px;
+}
+
+.pane-footer {
+  border-bottom: none;
+  border-bottom-left-radius: 4px;
+  border-bottom-right-radius: 4px;
+}
+
+.pane td {
+  padding: 0.25rem;
+  vertical-align: middle;
+}
+
+table.pane {
+  width: 100%;
+  border-collapse: collapse;
+  border: var(--pane-border-width) solid var(--pane-border-color);
+}
+
+td.pane {
+  padding: 0.25rem;
+  vertical-align: middle;
+}
+
