+<!DOCTYPE html>
+<html lang="en">
+<head>
+    <meta charset="UTF-8">
+    <title>DMN Editor Test</title>
+
+    <script>
+        function loadDiagram(diagram) {
+            var path = "somePath";
+            window.frames.editorFrame.contentWindow.gwtEditorBeans.get("DMNDiagramEditor").get().setContent(path, diagram);
+            document.getElementById("download_btn").disabled = false;
+        }
+
+        function download() {
+                window.frames.editorFrame.contentWindow.gwtEditorBeans.get("DMNDiagramEditor").get().getContent().then(
+                    function(p) {
+                        var d = document.createElement('a');
+                        d.setAttribute('href', 'data:text/xml;charset=utf-8,' + encodeURIComponent(p));
+                        d.setAttribute('download', "diagram.dmn");
+                        d.style.display = 'none';
+
+                        document.body.appendChild(d);
+                        d.click();
+                        document.body.removeChild(d);
+                    }
+                );
+        }
+
+        var openFile = function (event) {
+            var input = event.target;
+            var reader = new FileReader();
+            reader.onload = function () {
+                var diagram = reader.result;
+                loadDiagram(diagram)
+            };
+
+            reader.readAsText(input.files[0]);
+        };
+    </script>
+
+</head>
+<body>
+
+<div id="editor">
+    Wait until the editor is loaded to open a DMN file:<br>
+    <input type="button" onclick="loadDiagram('')" value="Create new Diagram"/>
+    or
+    <input type="file" onchange="openFile(event)" value="Choose DMN file" accept="dmn,txt"/>
+    When you are ready you can
+    <input type="button" id="download_btn" value="Download the diagram" onclick="download()" disabled/>
+</div>
+
+<iframe id="editorFrame" src="index.html" width="100%" height="1000px" frameBorder="0" scrolling="no"></iframe>
