+            // Add snapshot repository
+            url "https://oss.jfrog.org/artifactory/oss-snapshot-local"
+        }
+    }
+
+    googleJavaFormat {
+        toolVersion = '1.7'
+    }
+    compileJava.dependsOn(verifyGoogleJavaFormat)
