try {
        KNNIndexCacheEntry knnIndexCacheEntry = removalNotification.getValue();
               executor.execute(() -> knnIndexCacheEntry.getKnnIndex().close());
} finally {
       knnIndexCacheEntry.getFileWatcherHandle().stop();
}

