A snippet I've used on past projects
import { Mixed, TypeOf } from 'io-ts';
import { either, pipeable } from 'fp-ts';

/**
 * Takes unknown data and returns a strongly typed data.
 * If data does not match expected API interface, it throws a TypeError.
 *
 * @throws TypeError
 */
export const validateDataShape = <T extends Mixed>(data: unknown, validateShape: T): TypeOf<T> =>
  pipeable.pipe(
    validateShape.decode(data),
    either.fold(
      /**
       * Where there is an error, throw exception and log a message.
       * Example message: 'value 3 is unexpected type, at root > property > 2 > isActive,
       * actual type "string" expected type "boolean"'
       */
      errors => {
        throw new TypeError(
          errors
            .map(({ message, context, value }) => {
              const overallErrorDescription = message || `value ${value} is unexpected type, at`;
              return `${overallErrorDescription} ${context
                .map(({ key }) => key || 'root')
                .join(' > ')}, actual type "${typeof context[context.length - 1]
                .actual}" expected type "${context[context.length - 1].type.name}"`;
            })
            .join('; '),
        );
      },
      /**
       * Pass valid value through
       */
      value => value,
    ),
  );
export const fetchWrapped = async <T extends Mixed>({
  url,
  shape,
  token,
  requestOptions,
}: UseFetchProps<T>) => {
  const response = await fetch(url, generateRequestOptions(requestOptions, token));

  let data;

  try {
    data = await response.json();
  } catch (e) {
    // the server might respond with an empty body
    // in some cases, which should be ignored
  }

  if (data?.status >= 400) {
    throw new Error(data.statusText);
  }

  return validateData(data, shape);
};
where fetchWrapper is a rough equivalent to GET and fetch is the stand in for axios,
and where shape can be any io-ts definition, for example:
import * as t from 'io-ts';

export const DefinedTypeShape = t.strict({
  type: t.string,
  isActive: t.boolean,
});

