+    await act(async () => {
+      await getFacetsPromise;
+    });
+    component.update();
+  });
+  afterEach(() => jest.clearAllMocks());
+
+  describe("when the page is mounted", () => {
+    const getListItems = () => component.find(".MuiListItem-container");
