+    private void processCommand(BRC1HCommand command) {
+        logger.debug("[{}] ProcessCommand {}", super.thing.getUID().getId(), command.getClass().getSimpleName());
+
+        try {
+            currentCommand = command;
+            uartProcessor.abandon();
