+                fs = command.getHeatingFanSpeed();
+                break;
+            case HEAT:
+                fs = command.getHeatingFanSpeed();
+                break;
+            case COOL:
+                fs = command.getCoolingFanSpeed();
+                break;
+            default:
+                return;
+        }
+
+        if (fs == null) {
+            return;
+        }
+
+        // No need to re-set if it is the same value
