+                updateStateIfLinked(DaikinMadokaBindingConstants.CHANNEL_ID_HOMEKIT_TARGET_HEATING_COOLING_MODE,
+                        new StringType(newHomekitTargetStatus));
+            }
+
+        }
+    }
