+        if (valueList == null) {
+            return false;
+        }
+        Iterator<SensorDataValue> iter = valueList.iterator();
+        while (iter.hasNext()) {
+            SensorDataValue v = iter.next();
+            if (!(v.getValue_type().equals(P1) || v.getValue_type().equals(P2))) {
+                return false;
+            }
+        }
+        return true;

