+    private List<AreaProperties> discoverAreas()
+            throws OmniInvalidResponseException, OmniUnknownMessageTypeException, BridgeOfflineException {
+        ObjectPropertyRequest<AreaProperties> objectPropertyRequest = ObjectPropertyRequest
+                .builder(bridgeHandler, ObjectPropertyRequests.AREA).build();
+
+        List<AreaProperties> areas = new LinkedList<>();
+
+        for (AreaProperties areaProperties : objectPropertyRequest) {
+
+            int objnum = areaProperties.getNumber();
+
+            /*
+             * It seems that for simple OmniLink Controller configurations there is only 1 area, without a name. So if
+             * there is no
+             * name for the first area, we will call that Main. If other area's name is blank, we will not create a
+             * thing.
+             */
+            String areaName = areaProperties.getName();
+            if (areaProperties.getNumber() == 1 && "".equals(areaName)) {
+                areaName = "Main Area";
+            } else if ("".equals(areaName)) {
+                break;
+            }
+
+            Map<String, Object> properties = new HashMap<>();
+            ThingUID thingUID = new ThingUID();
+            final SystemType systemType = this.systemType;
+            if (systemType != null) {
+                switch (systemType) {
+                    case LUMINA:
+                        thingUID = new ThingUID(THING_TYPE_LUMINA_AREA, bridgeHandler.getThing().getUID(),
+                                Integer.toString(objnum));
+                        break;
+                    case OMNI:
+                        thingUID = new ThingUID(THING_TYPE_OMNI_AREA, bridgeHandler.getThing().getUID(),
+                                Integer.toString(objnum));
+                        break;
+                    default:
+                        throw new IllegalStateException("Unknown System Type");
+                }
+            }
+
+            properties.put(THING_PROPERTIES_NUMBER, objnum);
+            properties.put(THING_PROPERTIES_NAME, areaName);
+
+            DiscoveryResult discoveryResult = DiscoveryResultBuilder.create(thingUID).withProperties(properties)
+                    .withBridge(this.bridgeHandler.getThing().getUID()).withLabel(areaName).build();
+            thingDiscovered(discoveryResult);
+            areas.add(areaProperties);
+        }
+        return areas;
+    }
