		String prop = null;

		switch (param) {
			case "name":
				if (!containsAlias(criteria, "pn")) {
					criteria.createAlias("names", "pn");
				}

				return "pn.givenName";
			case "birthDate":
				return "birthdate";
			case "city":
				prop = "pad.cityVillage";
			case "state":
				prop = "pad.stateProvince";
			case "postalCode":
				prop = "pad.postalCode";
			case "country":
				prop = "pad.country";

                if (!containsAlias(criteria, "pad")) {
					criteria.createAlias("addresses", "pad");
