+        }
+        return false;
+    }
+
+    static boolean isDcLineSegment(AdjacentType type) {
+        return type == AdjacentType.DC_LINE_SEGMENT;
+    }
+
+    static boolean isAcDcConverter(AdjacentType type) {
+        return type == AdjacentType.AC_DC_CONVERTER;
+    }
