+            nodeEquipments.get(node).stream()
+                .filter(eq -> eq.type == TPnodeEquipments.EquipmentType.DC_LINE_SEGMENT)
+                .forEach(eq -> LOG.info("DcLineSegment {}", eq.equipmentId));
+        }
+    }
