+    static class TPnodeEquipment {
+        EquipmentType type;
+        String equipmentId;
+
+        TPnodeEquipment(EquipmentType type, String equipmentId) {
+            this.type = type;
+            this.equipmentId = equipmentId;
+        }
+
+        void print() {
+            LOG.info("    {} {}", this.type, this.equipmentId);
+        }
+    }
