+package com.facebook.presto.hive;
+
+import com.facebook.presto.cache.CacheConfig;
+import com.facebook.presto.spi.PrestoException;
+import com.google.inject.Inject;
+import io.airlift.units.DataSize;
+
+import java.util.Optional;
+
+import static com.facebook.presto.hive.CacheQuotaScope.GLOBAL;
+import static com.facebook.presto.hive.CacheQuotaScope.SCHEMA;
+import static com.facebook.presto.hive.CacheQuotaScope.TABLE;
+import static com.facebook.presto.hive.HiveErrorCode.HIVE_UNKNOWN_ERROR;
+import static java.lang.String.format;
+import static java.util.Objects.requireNonNull;
+
+public class ConfigBasedCacheQuotaProvider
+        implements CacheQuotaProvider
+{
+    private final CacheQuotaScope cacheQuotaScope;
+    private final Optional<DataSize> configuredCacheQuota;
+
+    @Inject
+    public ConfigBasedCacheQuotaProvider(CacheConfig cacheConfig)
+    {
+        requireNonNull(cacheConfig, "cacheConfig is null");
+        this.cacheQuotaScope = cacheConfig.getCacheQuotaScope();
+        this.configuredCacheQuota = cacheConfig.getDefaultCacheQuota();
+    }
+
+    @Override
+    public CacheQuotaRequirement getCacheQuota(String schema, String table)
+    {
+        switch (cacheQuotaScope) {
+            case GLOBAL:
+                return new CacheQuotaRequirement(GLOBAL, configuredCacheQuota);
+            case SCHEMA:
+                return new CacheQuotaRequirement(SCHEMA, configuredCacheQuota);
+            case TABLE:
+                return new CacheQuotaRequirement(TABLE, configuredCacheQuota);
+            default:
+                throw new PrestoException(HIVE_UNKNOWN_ERROR, format("%s is not supported", configuredCacheQuota));
