+package io.quarkus.gradle;
+
+import io.quarkus.test.devmode.util.DevModeTestUtils;
+import io.quarkus.utilities.JavaBinFinder;
+
+import java.io.File;
+import java.io.IOException;
+import java.nio.file.Path;
+import java.util.ArrayList;
+import java.util.List;
+import java.util.concurrent.Future;
+import java.util.function.Supplier;
+
+public class HttpUtils {
+
+    private static Future<?> quarkusDev;
+
+    protected static Process doLaunch(Path jar, File output) throws IOException {
+        List<String> commands = new ArrayList<>();
+        commands.add(JavaBinFinder.findBin());
+        commands.add("-jar");
+        commands.add(jar.toString());
+        ProcessBuilder processBuilder = new ProcessBuilder(commands.toArray(new String[0]));
+        processBuilder.redirectOutput(output);
+        processBuilder.redirectError(output);
+        return processBuilder.start();
+    }
+
+    protected static String getHttpResponse(String path) {
+        return DevModeTestUtils.getHttpResponse(path, getQuarkusDevBrokenReason());

