+import io.quarkus.runtime.configuration.ProfileManager;
+
+@Recorder
+public class SpringCloudConfigClientRecorder {
+
+    private static final Logger log = Logger.getLogger(SpringCloudConfigClientRecorder.class);
+
+    public RuntimeValue<ConfigSourceProvider> create(SpringCloudConfigServerClientConfig springCloudConfigServerClientConfig,
+            ApplicationConfig applicationConfig) {
+        if (!springCloudConfigServerClientConfig.enabled) {
