+            user_ptrs[i] = reinterpret_cast<int64_t>(java_user);
+        }
+
+        env->SetLongArrayRegion(java_users, 0, size, user_ptrs);
+        delete[] user_ptrs;
+        return java_users;
+    }
+    CATCH_STD()
+    return nullptr;
+}
