+              item.setAttempts(item.getAttempts() + 1);
+              item.setLastAttemptStarted(Instant.now());
+
+              FileConversionQueueItem inProgressItem = transactionTemplate.execute(status -> {
+                return (FileConversionQueueItem) sessionFactory.getCurrentSession().merge(item);
+              });
+
+              try {
+                String sourceFileName = ref.substring(ref.lastIndexOf("/") + 1);
+                log.debug("Source Filename: {}", sourceFileName);
+                String convertedFileName = "";
+                if (sourceFileName.contains(".")) {
+                  convertedFileName = sourceFileName.split("\\.")[0] + ".pdf";
+                } else {
+                  convertedFileName = sourceFileName + ".pdf";
+                }
+
+                int timeoutMillis = 5000;
+                RequestConfig config = RequestConfig.custom()
+                  .setConnectTimeout(timeoutMillis)
+                  .setConnectionRequestTimeout(timeoutMillis)
+                  .setSocketTimeout(timeoutMillis * 1000).build();
+                CloseableHttpClient client = HttpClientBuilder.create().setDefaultRequestConfig(config).build();
+
+                HttpPost httpPost = new HttpPost(converterBaseUrl + "/lool/convert-to/pdf");
+
+                HttpEntity multipart = MultipartEntityBuilder.create()
+                  .setMode(HttpMultipartMode.BROWSER_COMPATIBLE)
+                  .addBinaryBody("data", source.streamContent(), ContentType.MULTIPART_FORM_DATA, URLEncoder.encode(convertedFileName, StandardCharsets.UTF_8.toString()))
+                  .build();
+
+                httpPost.setEntity(multipart);
+                CloseableHttpResponse response = client.execute(httpPost);
+                byte[] convertedFileBytes = EntityUtils.toByteArray(response.getEntity());
+                client.close();
+
+                String previewId = ref.substring(0, ref.lastIndexOf("/")) + convertedFileName;
+
+                log.debug("Converted file id: {}", previewId);
+                ResourcePropertiesEdit properties = contentHostingService.newResourceProperties();
+                properties.addProperty(ResourceProperties.PROP_DISPLAY_NAME, convertedFileName);
+
+                securityService.pushAdvisor(securityAdvisor);
+                ContentResource previewResource
+                    = contentHostingService.addAttachmentResource(convertedFileName, "application/pdf", convertedFileBytes, properties);
+
+                contentHostingService.addProperty(ref, ContentHostingService.PREVIEW, previewResource.getId());
+
+                log.debug("Deleting item with ref {}. It's been successfully converted.", ref);
+                transactionTemplate.execute(new TransactionCallbackWithoutResult() {
+
+                  public void doInTransactionWithoutResult(TransactionStatus status) {
+                    FileConversionQueueItem di = (FileConversionQueueItem) sessionFactory.getCurrentSession().merge(inProgressItem);
+                    sessionFactory.getCurrentSession().delete(di);
+                  }
+                });
+              } catch (Exception e) {
+                item.setStatus(FileConversionStatus.NOT_STARTED.name());
+                transactionTemplate.execute(new TransactionCallbackWithoutResult() {
+
+                  public void doInTransactionWithoutResult(TransactionStatus status) {
+                    sessionFactory.getCurrentSession().merge(item);
+                  }
+                });
+                log.error("Call to conversion service failed", e);
+              } finally {
+                securityService.popAdvisor(securityAdvisor);
+              }
+            });
+          } catch (IdUnusedException iue) {
+            log.error("No resource found for ref {}", ref);
+          } catch (PermissionException pe) {
+            log.error("No permissions to get the resource or its properties. Resource: {}", ref, pe);
+          } catch (Exception e) {
+            log.error("Failed to convert resource for ref {}", ref, e);
+          }
+          try {
+            Thread.sleep(pause);
+          } catch (Exception se) {
+          }
+        });
+      } finally {
+        securityService.popAdvisor(securityAdvisor);
+      }
+    }, 0, queueIntervalMinutes, TimeUnit.MINUTES);
+  }
+
+  public void destroy() {
+
+    workers.shutdownNow();
+    master.shutdownNow();
+  }
+
+  /**
+   * @inheritDoc
+   */
+  public FileConversionStatus getStatus(String ref) {
+
+    if (!enabled) {
+      return FileConversionStatus.DISABLED;
+    }
+
+    List<FileConversionQueueItem> items = sessionFactory.getCurrentSession()
+      .createCriteria(FileConversionQueueItem.class)
+        .add(Restrictions.eq("reference", ref)).list();
+
+    if (items.isEmpty() || items.size() > 1) {
+      log.warn("There should be a max of one conversion for {}", ref);
