+        case "NOT_STARTED":
+          return FileConversionStatus.NOT_STARTED;
+        case "IN_PROGRESS":
+          return FileConversionStatus.IN_PROGRESS;
+        case "COMPLETED":
+          return FileConversionStatus.COMPLETED;
+        default:
+          return FileConversionStatus.UNKNOWN;
+      }
