+                    .withExposedPorts(KUDU_TSERVER_PORT)
+                    .withCommand("tserver")
+                    .withEnv("KUDU_MASTERS", "kudu-master:" + KUDU_MASTER_PORT)
+                    .withNetwork(network)
+                    .withNetworkAliases("kudu-tserver-" + instance)
