+import java.util.function.Consumer;
+import java.util.function.Function;
+
+import static com.google.common.base.Strings.repeat;
+import static com.google.common.base.Verify.verify;
+import static java.lang.Integer.min;
+import static java.lang.String.format;
+import static org.assertj.core.api.Assertions.assertThat;
+
+public class TestJdbcCompatibility
+{
+    private static final Optional<Integer> DRIVER_VERSION_TESTED = Optional.ofNullable(System.getenv("PRESTO_JDBC_VERSION")).map(Integer::valueOf);
+
+    private static final int DEFAULT_PRECISION = 3; // milliseconds
+    private static final int MAX_SQL_PRECISION = 9; // nanoseconds
+    private static final int MAX_TIMESTAMP_PRECISION = 12; // picoseconds
+    private static final String TESTED_TZ = "Australia/Eucla"; // GMT+8:45
+
+    private TestingPrestoServer server;
+    private String serverUrl;
+
+    @BeforeClass
+    public void setup()
+    {
+        Logging.initialize();
+
+        server = TestingPrestoServer.builder()
+                .build();
+
+        serverUrl = format("jdbc:presto://%s", server.getAddress());
+    }
+
+    @AfterClass(alwaysRun = true)
+    public void tearDown()
+            throws IOException
+    {
+        server.close();
+    }
+
+    @Test
+    public void ensureProperDriverVersionLoaded()
+    {
+        if (DRIVER_VERSION_TESTED.isEmpty()) {
+            throw new SkipException("PRESTO_JDBC_VERSION environment variable is missing");
+        }
+
+        assertThat(PrestoDriver.class.getPackage().getImplementationVersion())
+                .isEqualTo(Integer.toString(DRIVER_VERSION_TESTED.get()));
+    }
+
+    @Test
+    public void ensureUsingProperCliVersion()
+            throws ClassNotFoundException
+    {
+        if (DRIVER_VERSION_TESTED.isEmpty()) {
+            throw new SkipException("PRESTO_JDBC_VERSION environment variable is missing");
+        }
+
+        assertThat(Class.forName("io.prestosql.jdbc.$internal.client.StatementClientFactory").getPackage().getImplementationVersion())
+                .isEqualTo(Integer.toString(DRIVER_VERSION_TESTED.get()));
+    }
+
+    @Test
+    public void testLongPreparedStatement()
+            throws Exception
+    {
+        String sql = format("SELECT '%s' = '%s'", repeat("x", 100_000), repeat("y", 100_000));
+
+        try (ResultSet rs = runQuery(sql)) {
+            assertThat(rs.next()).isTrue();
+            assertThat(rs.getBoolean(1)).isFalse();
+            assertThat(rs.next()).isFalse();
+        }
+    }
+
+    @Test
+    public void testTimestampSelect()
+    {
+        String query = "SELECT timestamp '2012-10-31 01:00'";
+        assertQueryResultEquals(query, Timestamp.valueOf("2012-10-31 01:00:00.000"), TestJdbcCompatibility::getSingleElementAsTimestamp);
+    }
+
+    @Test
+    public void testTimestampWithTzSelect()
+    {
+        if (driverVersion() == 335 || driverVersion() == 336) {
+            throw new SkipException("Reading timestamp with time zone is broken in 335 and 336");
+        }
+
+        String query = format("SELECT timestamp '2012-10-31 01:00 %s'", TESTED_TZ);
+        assertQueryResultEquals(query, Timestamp.valueOf("2012-10-30 10:15:00.000"), TestJdbcCompatibility::getSingleElementAsTimestamp);
+    }
+
+    @Test
+    public void testSelectParametricTimestampAsSqlTimestamp()
+    {
+        String timestamp = "2421-01-19 15:55:23.393456298901";
+
+        for (int precision = 0; precision <= MAX_TIMESTAMP_PRECISION; precision++) {
+            String query = format("SELECT cast(TIMESTAMP '%s' as TIMESTAMP(%d))", timestamp, precision);
+            Timestamp expectedTimestamp = roundedSqlTimestamp(timestamp, precision, MAX_SQL_PRECISION);
+
+            assertQueryResultEquals(query, expectedTimestamp, TestJdbcCompatibility::getSingleElementAsTimestamp);
+        }
+    }
+
+    @Test
+    public void testSelectParametricTimestampTzAsSqlTimestamp()
+    {
+        if (driverVersion() == 335 || driverVersion() == 336) {
+            throw new SkipException("Reading timestamp with time zone is broken in 335 and 336");
+        }
+
+        String timestamp = "2421-01-19 15:55:23.393456298901";
+        String localTimestamp = "2421-01-19 01:10:23.393456298901";
+
+        for (int precision = 0; precision <= MAX_TIMESTAMP_PRECISION; precision++) {
+            String query = format("SELECT cast(TIMESTAMP '%s %s' as TIMESTAMP(%d) WITH TIME ZONE)", timestamp, TESTED_TZ, precision);
+            Timestamp expectedTimestamp = roundedSqlTimestamp(localTimestamp, precision, MAX_SQL_PRECISION);
+
+            assertQueryResultEquals(query, expectedTimestamp, TestJdbcCompatibility::getSingleElementAsTimestamp);
+        }
+    }
+
+    @Test
+    public void testSelectParametricTimestampInMap()
+    {
+        String timestamp = "2304-08-24 23:55:23.383345789012";
+
+        for (int precision = 0; precision <= MAX_TIMESTAMP_PRECISION; precision++) {
+            String query = format("SELECT map_from_entries(ARRAY[('timestamp', cast(TIMESTAMP '%s' as TIMESTAMP(%d)))])", timestamp, precision);
+            String expectedTimestamp = roundedTimestamp(timestamp, precision, MAX_TIMESTAMP_PRECISION);
+
+            assertQueryResultEquals(query, expectedTimestamp, TestJdbcCompatibility::getSingleElementFromMap);
+            assertDescribeType(query, "map(varchar(9), %s)", precision, false);
+        }
+    }
+
+    @Test
+    public void testSelectParametricTzTimestampInMap()
+    {
+        String timestamp = "2304-08-24 23:55:23.383345789012";
+
+        for (int precision = 0; precision <= MAX_TIMESTAMP_PRECISION; precision++) {
+            String query = format("SELECT map_from_entries(ARRAY[('timestamp', cast(TIMESTAMP '%s %s' as TIMESTAMP(%d) WITH TIME ZONE))])", timestamp, TESTED_TZ, precision);
+            String expectedTimestamp = roundedTimestamp(timestamp, precision, MAX_TIMESTAMP_PRECISION);
+
+            assertQueryResultEquals(query, expectedTimestamp + " " + TESTED_TZ, TestJdbcCompatibility::getSingleElementFromMap);
+            assertDescribeType(query, "map(varchar(9), %s)", precision, true);
+        }
+    }
+
+    @Test
+    public void testSelectParametricTimestampInArray()
+    {
+        String timestamp = "2304-08-24 23:55:23.383345789012";
+
+        for (int precision = 0; precision <= MAX_TIMESTAMP_PRECISION; precision++) {
+            String query = format("SELECT ARRAY[cast(TIMESTAMP '%s' as TIMESTAMP(%d))]", timestamp, precision);
+            String expectedTimestamp = roundedTimestamp(timestamp, precision, MAX_TIMESTAMP_PRECISION);
+
+            assertQueryResultEquals(query, expectedTimestamp, TestJdbcCompatibility::getSingleElementFromArray);
+            assertDescribeType(query, "array(%s)", precision, false);
+        }
+    }
+
+    @Test
+    public void testSelectParametricTimestampTzInArray()
+    {
+        String timestamp = "2304-08-24 23:55:23.383345789012";
+
+        for (int precision = 0; precision <= MAX_TIMESTAMP_PRECISION; precision++) {
+            String query = format("SELECT ARRAY[cast(TIMESTAMP '%s %s' as TIMESTAMP(%d) WITH TIME ZONE)]", timestamp, TESTED_TZ, precision);
+            String expectedTimestamp = roundedTimestamp(timestamp, precision, MAX_TIMESTAMP_PRECISION);
+
+            assertQueryResultEquals(query, expectedTimestamp + " " + TESTED_TZ, TestJdbcCompatibility::getSingleElementFromArray);
+            assertDescribeType(query, "array(%s)", precision, true);
+        }
+    }
+
+    @Test
+    public void testSelectParametricTimestampInRow()
+    {
+        String timestamp = "2304-08-24 23:55:23.383345789012";
+
+        for (int precision = 0; precision <= MAX_TIMESTAMP_PRECISION; precision++) {
+            String query = format("SELECT CAST(ROW(TIMESTAMP '%s') AS ROW(timestamp TIMESTAMP(%d)))", timestamp, precision);
+            String expectedTimestamp = roundedTimestamp(timestamp, precision, MAX_TIMESTAMP_PRECISION);
+
+            assertQueryResultEquals(query, expectedTimestamp, TestJdbcCompatibility::getSingleElementFromRow);
+            assertDescribeType(query, "row(timestamp %s)", precision, false);
+        }
+    }
+
+    @Test
+    public void testSelectParametricTzTimestampInRow()
+    {
+        String timestamp = "2304-08-24 23:55:23.383345789012";
+
+        for (int precision = 0; precision <= MAX_TIMESTAMP_PRECISION; precision++) {
+            String query = format("SELECT CAST(ROW(TIMESTAMP '%s %s') AS ROW(timestamp TIMESTAMP(%d) WITH TIME ZONE))", timestamp, TESTED_TZ, precision);
+            String expectedTimestamp = roundedTimestamp(timestamp, precision, MAX_TIMESTAMP_PRECISION);
+
+            assertQueryResultEquals(query, expectedTimestamp + " " + TESTED_TZ, TestJdbcCompatibility::getSingleElementFromRow);
+            assertDescribeType(query, "row(timestamp %s)", precision, true);
+        }
+    }
+
+    @Test
+    public void testSelectParametricTimestampAsString()
+    {
+        String timestamp = "2020-07-16 15:55:23.383345789012";
+
+        for (int precision = 0; precision <= MAX_TIMESTAMP_PRECISION; precision++) {
+            String query = format("SELECT cast(TIMESTAMP '%s' as TIMESTAMP(%d))", timestamp, precision);
+            String expectedTimestamp = roundedTimestamp(timestamp, precision, MAX_TIMESTAMP_PRECISION);
+
+            assertQueryResultEquals(query, expectedTimestamp, TestJdbcCompatibility::getFirstString);
+            assertDescribeType(query, "%s", precision, false);
+        }
+    }
+
+    @Test
+    public void testSelectParametricTimestampTzAsString()
+    {
+        String timestamp = "2020-07-16 15:55:23.383345789012";
+
+        for (int precision = 0; precision <= MAX_TIMESTAMP_PRECISION; precision++) {
+            String query = format("SELECT cast(TIMESTAMP '%s %s' as TIMESTAMP(%d) WITH TIME ZONE)", timestamp, TESTED_TZ, precision);
+            String expectedTimestamp = roundedTimestamp(timestamp, precision, MAX_TIMESTAMP_PRECISION);
+
+            assertQueryResultEquals(query, expectedTimestamp + " " + TESTED_TZ, TestJdbcCompatibility::getFirstString);
+            assertDescribeType(query, "%s", precision, true);
+        }
+    }
+
+    public void assertQueryResultEquals(String query, Object expectedTimestamp, Function<ResultSet, Object> extractValue)
+    {
+        try (ResultSet rs = runQuery(query)) {
+            assertThat(rs.next()).isTrue();
+            assertThat(extractValue.apply(rs)).isEqualTo(expectedTimestamp);
+            assertThat(rs.next()).isFalse();
+        }
+        catch (Exception e) {
+            throw new RuntimeException(e);
+        }
+    }
+
+    private void assertDescribeType(String query, String expectedTypePattern, int precision, boolean withTimeZone)
+    {
+        assertDescribedInputType(query, format(expectedTypePattern, describeTimestampType(precision, true, withTimeZone)), true);
+        assertDescribedInputType(query, format(expectedTypePattern, describeTimestampType(precision, false, withTimeZone)), false);
+        assertDescribedOutputType(query, format(expectedTypePattern, describeTimestampType(precision, true, withTimeZone)), true);
+        assertDescribedOutputType(query, format(expectedTypePattern, describeTimestampType(precision, false, withTimeZone)), false);
+    }
+
+    private void assertDescribedInputType(String query, String expectedType, boolean ommitPrecision)
+    {
+        if (ommitPrecision && !supportsSessionProperties()) {
+            // Driver does not support setting session properties
+            return;
+        }
+
+        runConnection(connection -> {
+            try {
+                connection.prepareStatement(format("SELECT 1 FROM (%s AS timestamp) WHERE timestamp = ?", query));
+
+                try (ResultSet resultSet = connection.prepareStatement("DESCRIBE INPUT statement1").executeQuery()) {
+                    assertThat(resultSet.next()).isTrue();
+                    assertThat(resultSet.getString(2)).isEqualTo(expectedType);
+                }
+            }
+            catch (SQLException e) {
+                throw new RuntimeException(e);
+            }
+        }, omitDateTimeTypePrecision(ommitPrecision));
+    }
+
+    private void assertDescribedOutputType(String query, String expectedType, boolean ommitPrecision)
+    {
+        if (ommitPrecision && !supportsSessionProperties()) {
+            // Driver does not support setting session properties
+            return;
+        }
+
+        runConnection(connection -> {
+            try {
+                connection.prepareStatement(query);
+
+                try (ResultSet resultSet = connection.prepareStatement("DESCRIBE OUTPUT statement1").executeQuery()) {
+                    assertThat(resultSet.next()).isTrue();
+                    assertThat(resultSet.getString(5)).isEqualTo(expectedType);
+                }
+            }
+            catch (SQLException e) {
+                throw new RuntimeException(e);
+            }
+        }, omitDateTimeTypePrecision(ommitPrecision));
+    }
+
+    private static String describeTimestampType(int precision, boolean ommitPrecision, boolean withTimezone)
+    {
+        if (ommitPrecision && precision == DEFAULT_PRECISION) {
+            if (withTimezone) {
+                return "timestamp with time zone";
+            }
+
+            return "timestamp";
+        }
+
+        if (withTimezone) {
+            return format("timestamp(%d) with time zone", precision);
+        }
+
+        return format("timestamp(%d)", precision);
+    }
+
+    private static Timestamp roundedSqlTimestamp(String timestamp, int precision, int maxPrecision)
+    {
+        return Timestamp.valueOf(roundedTimestamp(timestamp, precision, maxPrecision));
+    }
+
+    private static String getFirstString(ResultSet resultSet)
+    {
+        try {
+            return resultSet.getString(1);
+        }
+        catch (SQLException e) {
+            throw new RuntimeException(e);
+        }
+    }
+
+    private static Object getSingleElementFromArray(ResultSet resultSet)
+    {
+        try {
+            Array array = resultSet.getArray(1);
+            return ((Object[]) array.getArray(1, 1))[0];
+        }
+        catch (SQLException e) {
+            throw new RuntimeException(e);
+        }
+    }
+
+    private static Object getSingleElementFromRow(ResultSet resultSet)
+    {
+        try {
+            Map<String, Object> row = (Map<String, Object>) resultSet.getObject(1);
+            return row.get("timestamp");
