+    @BeforeClass(description = "initialise the setup")
+    void start() throws Exception {
+        super.startMGW();
+
+        //deploy the api
+        //api yaml file should put to the resources/apis/openApis folder
+        String prodSandApiZipfile = ApiProjectGenerator.createApictlProjZip(
