            public String extract(Request request, Connection connection) {
                String url = request.url().toString();
                String regex = "/okhttp/back/";
                if (url.contains(regex)) {
                    url = url.substring(0, url.indexOf(regex) + regex.length()) + "{id}";
                }
                return url;
            }
