+    @Nullable
+    private Object[] newData(ForeignNote n) {
+        long id = mNextId;
+        mNextId++;
+        _ids.add(id);
+        if (!processFields(n)) {
+            return null;
+        }
+        for (Map.Entry<Integer, ForeignCard> a : n.cards.entrySet()) {
+            _cards.add(new Triple(id, a.getKey(), a.getValue()));
+        }
+        return new Object[] {
+                id,
+                guid64(),
+                mModel.getLong("id"),
+                intTime(),
+                mCol.usn(),
+                mCol.getTags().join(n.mTags),
+                n.fieldsStr,
+                "",
+                "",
+                0,
+                ""
+        };
+    }
