  "reader": {
    "name": "mysqlreader",
    "parameter": {
      "username": "${db_user}",
      "password": "${db_pwd}",
      "encoding": "UTF-8",
      "column": [${tbl_clms}],
      "connection": [
        {
          "jdbcUrl": [
            "jdbc:mysql://${host}/${db}?useUnicode=true&characterEncoding=utf8"
          ],
          "table": [
            ${table_list}
          ]
        }
      ]
    }
  }
  ...
}

