+	private static final Pattern dynamicIndexPattern = Pattern.compile(".*\\{.+\\}.*");
+	private static final Pattern dynamicIndexTimeExtractPattern = Pattern.compile(".*\\{.+\\|.*\\}.*");
+	private static final String DEFAULT_DATE_FORMAT = "yyyy-MM-dd";
+
