+        if (!commitId.equals(EnvironmentInformation.UNKNOWN_COMMIT_ID)) {
+            return commitId;
+        }
+
+        LOG.warn(
+                "Cannot initialize maven property test.randomization.seed with commit id, please set manually to receive reproducible builds.");
+        // return any constant
+        return "";
+    }
