+    private static FileObject getSubDir(FileObject dir, String relPath) {
+        FileObject subDir = dir.getFileObject(relPath);
+        if (subDir == null) {
+            try {
+                subDir = FileUtil.createFolder(dir, relPath);
+            } catch (IOException ex) {
+                Exceptions.printStackTrace(ex);
+            }
+        }
+        return subDir;
+    }
