+    private static FileObject findPrjDir(FileObject dir, String prjName, MxProject prj) {
+        if (dir == null) {
+            return null;
+        }
+        if (prj.dir() != null) {
+            return dir.getFileObject(prj.dir());
+        }
+        if (prj.subDir() != null) {
