+    OzoneConfiguration configuration = scm.getConfiguration();
+    long transferBandwidth = configuration.getLongBytes(
+        ScmConfigKeys.SCM_DB_CHECKPOINT_TRANSFER_RATE_KEY,
+        ScmConfigKeys.SCM_DB_CHECKPOINT_TRANSFER_RATE_DEFAULT);
