+        if (parameters == null || parameters.getParent().getPath() != ExecutionPath.rootPath()) {
+            return;
+        }
+        dealException(t);
+    }
+
+    private void dealException(Throwable throwable) {
+        AbstractSpan span = ContextManager.activeSpan();
+        span.errorOccurred();
+        span.log(throwable);
