+public final class SsoCredentialsProvider implements AwsCredentialsProvider, SdkAutoCloseable {
+
+    private static final Duration DEFAULT_STALE_TIME = Duration.ofMinutes(1);
+    private static final Duration DEFAULT_PREFETCH_TIME = Duration.ofMinutes(5);
+
+    private static final String ASYNC_THREAD_NAME = "sso-credentials-provider";
+
+    private final RuntimeException loadException;
+
+    private final SsoClient ssoClient;
+    private final Duration staleTime;
+    private final Duration prefetchTime;
+
+    private final CachedSupplier<SsoCredentialsHolder> credentialCache;
+
+    /**
+     * @see #builder()
+     */
+    SsoCredentialsProvider(BuilderImpl builder) {
+        this.ssoClient = Validate.notNull(builder.ssoClient, "SSO client must not be null.");
+
+        this.staleTime = Optional.ofNullable(builder.staleTime).orElse(DEFAULT_STALE_TIME);
+        this.prefetchTime = Optional.ofNullable(builder.prefetchTime).orElse(DEFAULT_PREFETCH_TIME);
+
+        RuntimeException loadException = null;
+        CachedSupplier.Builder<SsoCredentialsHolder> cacheBuilder = null;
+        try {
+            String profileName = builder.profileName != null ? builder.profileName
+                                                      : ProfileFileSystemSetting.AWS_PROFILE.getStringValueOrThrow();
+
+            ProfileFile profileFile = Optional.ofNullable(builder.profileFile)
+                                  .orElseGet(builder.defaultProfileFileLoader);
+
+            // Load Profile from the profile file
+            Optional<Profile> profileOptional = profileFile.profile(profileName);
+            if (!profileOptional.isPresent()) {
