+     * are close to expiring.
+     */
+    private RefreshResult<SsoCredentialsHolder> updateSsoCredentials(Profile profile) {
+        SsoCredentialsHolder credentials = new SsoCredentialsHolder(
+            (AwsSsoCredentials) new SsoCredentialsUtils(profile, ssoClient).getUpdatedCredentials());
+        Instant acutalTokenExpiration = credentials.getSsoCredentialsExpiration().toInstant();
+
+        return RefreshResult.builder(credentials)
+                            .staleTime(acutalTokenExpiration.minus(staleTime))
+                            .prefetchTime(acutalTokenExpiration.minus(prefetchTime))
+                            .build();
