   private ParallelDocumentQueryExecutionContextBase<T> getContext(IDocumentQueryExecutionComponent<T> component) {
    if (component instanceof GroupByDocumentQueryExecutionContext) {
        return this.getContext(((GroupByDocumentQueryExecutionContext<T>) component).getComponent());
    } if (component instanceof DistinctDocumentQueryExecutionContext) {
        return this.getContext(((DistinctDocumentQueryExecutionContext<T>) component).getComponent());
    } else if (component instanceof AggregateDocumentQueryExecutionContext) {
        return this.getContext(((AggregateDocumentQueryExecutionContext<T>) component).getComponent());
    } else if (component instanceof SkipDocumentQueryExecutionContext) {
        return this.getContext(((SkipDocumentQueryExecutionContext<T>) component).getComponent());
    } else {
        return (ParallelDocumentQueryExecutionContextBase<T>) component;
    }
