+	static final Class<? extends AssertionError>	JUNIT4_COMPARISON_FAILURE;
+	static final Class<? extends AssertionError>	ASSERTION_FAILED_ERROR;
+	static final Class<? extends AssertionError>	MULTIPLE_FAILURES_ERROR;
+
+	@SuppressWarnings("unchecked")
+	static Class<? extends AssertionError> tryLoad(String name) {
+		try {
+			return (Class<? extends AssertionError>) Class.forName(name);
+		} catch (ClassNotFoundException cnfe) {
+			return null;
+		}
+	}
+
