+public class SystemProgramManagementService extends AbstractScheduledService {
+
+  private static final Logger LOG = LoggerFactory.getLogger(SystemProgramManagementService.class);
+
+  private final long scheduleInterval;
+  private final CConfiguration cConf;
+  private final ProgramRuntimeService programRuntimeService;
+  private final ProgramLifecycleService programLifecycleService;
+  private final AtomicReference<Map<ProgramId, Map<String, String>>> programsEnabled;
+
+  @Inject
+  SystemProgramManagementService(CConfiguration cConf, ProgramRuntimeService programRuntimeService,
+                                 ProgramLifecycleService programLifecycleService) {
+    this.cConf = cConf;
+    this.scheduleInterval = cConf.getLong(Constants.AppFabric.PROGRAM_SCAN_INTERVAL);
+    this.programRuntimeService = programRuntimeService;
+    this.programLifecycleService = programLifecycleService;
+    this.programsEnabled = new AtomicReference<>();
+  }
+
+  /**
+   * Sets the map of services that are currently enabled along with their runtime args.
+   * The services that are not present in map will be stopped during the next run of the service.
+   *
+   * @param programsEnabled
+   */
+  public void setProgramsEnabled(Map<ProgramId, Map<String, String>> programsEnabled) {
+    this.programsEnabled.set(new HashMap<>(programsEnabled));
+  }
+
+  @Override
+  protected void runOneIteration() throws Exception {
+    if (programsEnabled.get() == null) {
+      LOG.debug("Services to run not yet set");
+      return;
+    }
+    try {
+      reconcileServices();
+    } catch (Exception ex) {
+      //catch here so a single iteration doesn't stop the service
+      LOG.error("Error in reconciling services", ex);
+    }
+  }
+
+  private void reconcileServices() throws Exception {
+    //take a copy
+    Map<ProgramId, Map<String, String>> enabledServicesMap = new HashMap<>(this.programsEnabled.get());
+    Set<ProgramId> enabledServices = enabledServicesMap.keySet();
+    Set<ProgramId> servicesToStop = new HashSet<>();
+    Set<ProgramId> servicesRunning = new HashSet<>();
+    Map<ProgramId, List<ProgramRuntimeService.RuntimeInfo>> programRunsMap = new HashMap<>();
+    Map<ProgramId, RunRecordDetail> programLatestRunMap = new HashMap<>();
+    //Get all current runs
+    List<ProgramRuntimeService.RuntimeInfo> runtimeInfos = programRuntimeService.listAll(ProgramType.SERVICE);
+    //Find services to delete and already running services
+    for (ProgramRuntimeService.RuntimeInfo runtimeInfo : runtimeInfos) {
+      ProgramId programId = runtimeInfo.getProgramId();
+      if (!programId.getNamespaceId().equals(NamespaceId.SYSTEM)) {
+        //We care only about system services
+        continue;
+      }
+      if (!enabledServices.contains(programId)) {
+        servicesToStop.add(programId);
+      } else {
+        servicesRunning.add(programId);
+        //gather run records to stop extra runs for each program
+        gatherRunRecords(programRunsMap, programLatestRunMap, runtimeInfo, programId);
+      }
+    }
+    //find services to start
+    Set<ProgramId> servicesToStart = enabledServices.stream()
+      .filter(programId -> !servicesRunning.contains(programId))
+      .collect(Collectors.toSet());
+    //start services
+    startServices(servicesToStart, enabledServicesMap);
+    //stop services
+    servicesToStop.forEach(this::stopService);
+    //stop extra runs for enabled services
+    pruneRuns(programRunsMap, programLatestRunMap);
+  }
