+      .getRunRecordMeta(runtimeInfo.getController().getProgramRunId());
+    programLastRunMap.putIfAbsent(programId, runRecordMeta);
+    programLastRunMap.put(programId, getLatestRunRecord(programLastRunMap.get(programId), runRecordMeta));
+  }
+
+  private void startServices(Set<ProgramId> servicesToStart, Map<ProgramId, Map<String, String>> enabledServicesMap) {
+    for (ProgramId programId : servicesToStart) {
+      Map<String, String> overrides = enabledServicesMap.get(programId);
+      LOG.debug("Starting program {} with args {}", programId, overrides);
+      try {
+        programLifecycleService.start(programId, overrides, false);
+      } catch (Exception ex) {
+        LOG.error("Could not start program", ex);
+      }
+    }
+  }
