+        if (null != name && CHECK_SUFFIXES.containsKey(name)) {
+            throw new IllegalArgumentException(String.format(
+                Locale.ENGLISH, "a check constraint [%s] already exists", name));
+        }
+        this.name = name;
+        this.expression = expression;
+    }
