+          if (tccMatrix.get(i).get(j).name == "" || tccMatrix.get(i).get(j).content == "") {
+            return false;
+          }
+          currentTableContent = currentTableContent + tccMatrix.get(i).get(j).name + SEPARATOR
+              + tccMatrix.get(i).get(j).content;
+        } else {
+          if (tccMatrix.get(i).get(j).name == "" || tccMatrix.get(i).get(j).content == "") {
