+	private List<String> extraDirectories = new ArrayList<>();
+
+	public void addExtraDirectory(String directory) {
+		extraDirectories.add(directory);
+	}
+
+	public void addExclude(String exclude) {
+		excludes.add(exclude);
+	}
+
