+        });
+        SourceSet mainSourceSet = sourceSets.getByName("main");
+        SourceSet testSourceSet = sourceSets.getByName("test");
+
+        extendConfiguration(project, testSourceSet, extraTestSourceSet, SourceSet::getCompileConfigurationName);

