+    private LogEntryBuilder securityChangeLogEntryBuilder(String requestId) {
+        return new LogEntryBuilder(false)
+                .with(EVENT_TYPE_FIELD_NAME, SECURITY_CHANGE_ORIGIN_FIELD_VALUE)
+                .withRequestId(requestId);
+    }
