+        double afterDecimal = newValue.doubleValue() - floor(newValue.doubleValue());
+        int minutes = (int) (afterDecimal * 60);
+
+        // set the calendar for given hour and minute from slider value
+        calendar.set(Calendar.HOUR_OF_DAY, hours);
