+                if (!serviceGeodatabase.getVersionName().equals(defaultVersion)) {
+                  editFeatureVBox.setDisable(false);
+                }
+              } else {
+                new Alert(Alert.AlertType.ERROR, "Feature failed to load.").show();
+              }
+            });
+          }
+        }
